Array.prototype.add = function(arr) {
  var res = [];
  for(var i = 0; i < this.length || i < arr.length; i++) {
    if(this[i] === undefined) {
      res.push(arr[i]);
    } else if(arr[i] === undefined) {
      res.push(this[i]);
    } else {
      res.push(this[i] + arr[i]);
    }
  };
  return res;
};

